package com.naver;

public class Monster {
	
	private int hp;
	private String name;
	
	public Monster() {
		// TODO Auto-generated constructor stub
	}
	public Monster(int hp, String name) {
		super();
		this.hp = hp;
		this.name = name;
	}
	public int getHp() {
		return hp;
	}
	public void setHp(int hp) {
		this.hp = hp;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	
	
	
	

}
